import React, {useState, useEffect} from 'react'
import './statusboard.scss'
import {Modal} from "../Modal/Modal";


const StatusBoard = (props) => {

    const [namesOfColumns, setNamesOfColumns] = useState([]);
    const [data, setData] = useState([]);
    const [modalActive, setModalActive]  = useState(false);
    const [inputValues, setInputValues] = useState({});
    let arrayOfInputsValue = {};

    useEffect(() => {
       fetch('http://localhost:3002/board-titles')
           .then((res) => res.json())
           .then((name) => setNamesOfColumns(name))
       fetch("http://localhost:3002/board-data")
           .then((res) => res.json())
           .then((data) => setData(data))
    }, [])

    const makeArrayOfInputs = (id, v) => {
        arrayOfInputsValue = inputValues;
        if (Object.keys(arrayOfInputsValue).length > 0)
            for (let i in arrayOfInputsValue)
            {
                if (i===id)
                {
                    arrayOfInputsValue[id] = v;
                }
                else Object.defineProperty(arrayOfInputsValue, id, {value: v, enumerable : true, writable: true});
            }
        else Object.defineProperty(arrayOfInputsValue, id, {value: v, enumerable : true, writable: true});
        // Object.assign(arrayOfInputsValue, temp)
        console.log(arrayOfInputsValue);
        setInputValues(arrayOfInputsValue);
    }
    const requestToServer = () => {
        console.log(inputValues)
        const formData = new FormData();
        Object.assign(formData, inputValues)
        fetch('http://localhost:3002/post-request', {
            mode: 'cors',
            method: 'POST',
            headers: {"Content-Type": "application/x-www-form-urlencoded", "boundary": "something"},
            body: JSON.stringify(formData)})
            .then((response) => response.json())
            }


    return (
        <div className="status-board">
            <div className="status-board-tools">
                <div className="title">{props.title}</div>
                <div className="tools">
                    {
                        props.dataOfTools.map((img_link,index) => (
                            <div key={index} className="tool">
                                <img src={`/img/${img_link}`} alt="lol"/>
                            </div>
                        )
                )}
                </div>
            </div>
            <div className="table">
                <div className="table-columns">
                    <div className="table-logo">
                        <table>
                            <tbody>
                            {data.map(({ellipse, img_link}, index) => (
                                <tr key={index}>
                                    <td><div className="checkbox"><input type="checkbox"/></div></td>
                                    <td><div className="border-box">
                                        <img className="ellipse" src="/img/ellipse.png" alt=""/>
                                        <img className="picture" src={img_link} alt=""/>
                                    </div></td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                    <div className="table-data">
                        <table>
                            <thead>
                            <tr>
                                {namesOfColumns.map(({title, vector},index) => (
                                    <th className="column-name" key={index}>{title}
                                        <span className="vector"><img src={vector} alt=""/></span>
                                    </th>
                                ))}
                            </tr>
                            </thead>
                            <tbody>
                                {data.map(({company, position, duration, job_id, status},index) => (
                                    <tr key={index}>
                                        <td id="table-data">{company}</td>
                                        <td id="table-data">{position}</td>
                                        <td id="table-data">{duration}</td>
                                        <td id="table-data">{job_id}</td>
                                        <td id="table-data">{status}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    <div className="points">
                        <table>
                            <tbody>
                                {data.map((data, index) => (
                                    <tr key={index}>
                                        <td id="table-points">
                                            <img src="/img/dot-vertical.png" alt=""/>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            <button onClick={() => setModalActive(true)}>Add new data</button>
            <Modal active={modalActive} setActive={setModalActive}>
                <div className="container">
                    <div className="closeBox">
                        <div className="cancel-button" onClick={() => setModalActive(false)}>
                            <img src="/img/closebox.png" alt=""/>
                        </div>
                    </div>
                    <div className="content">
                        <form action="http://localhost:3002/post-request" method="POST" id="form-to-request" encType="multipart/form-data">
                            <div className="inputs">
                                <label htmlFor="img_link">Logo</label>
                                <input name = "img_link" type="file" onChange={(e) => makeArrayOfInputs(e.target.name, e.target.files[0].name)}/>
                            </div>
                            <div className="inputs">
                                <label htmlFor="company">Company</label>
                                <input name = "company" type="text" onChange={(e) => makeArrayOfInputs(e.target.name, e.target.value)}/>
                            </div>
                            <div className="inputs">
                                <label htmlFor="position">Position</label>
                                <input name = "position" type="text" onChange={(e) => makeArrayOfInputs(e.target.name, e.target.value)}/>
                            </div>
                            <div className="inputs">
                                <label htmlFor="duration">Duration</label>
                                <input name = "duration" type="text" onChange={(e) => makeArrayOfInputs(e.target.name, e.target.value)}/>
                            </div>
                            <div className="inputs">
                                <label htmlFor="job_id">Job_id#</label>
