import './App.css';
import Top from "./components/TopComponent/Top";
import BottomComponent from "./components/BottomComponent/BottomComponent";
import React, {useEffect, useState} from 'react';

function App() {
    
    const element = document.getElementById('scrollable-element');

    element.addEventListener('scroll', function(e) {
        const scrollTop = element.scrollTop;
        const scrollHeight = element.scrollHeight;
        const height = element.clientHeight;
        const scrollPercentage = (scrollTop / (scrollHeight - height)) * 100;

        // Обновляем стили скроллбара в зависимости от положения скролла
        document.documentElement.style.setProperty('--scroll-percentage', scrollPercentage + '%');
    });

    return (
    <div className="App">
      <header className="App-header">
      </header>
        <div className="container">
            <Top/>
            <BottomComponent />
        </div>
    </div>
  );
}

export default App;
